import { Card, Carousel, Empty, Image } from 'antd';
import { apiMovies } from 'api/api';
import React, { useCallback, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import css from './Cast.module.css';

const Cast = () => {
  const [actors, setActors] = useState([]);
  const { movieId } = useParams();

  const getMovieDetails = useCallback(async () => {
    if (!movieId) {
      return;
    }

    const { cast } = await apiMovies('movie/', movieId + '/credits');
    setActors(cast);
  }, [movieId]);

  useEffect(() => {
    getMovieDetails();
  }, [getMovieDetails]);

  const isImg = () => {
    let isNullImg = 0;
    actors.map(({ profile_path }) => !profile_path && isNullImg++);
    return isNullImg;
  };

  return (
    <div className={css.wrapperMain}>
      {isImg() !== actors.length && (
        <Carousel autoplay className={css.wrapperCarousel}>
          {actors?.length > 0 &&
            actors.map(({ id, name, profile_path, character }) => {
              if (!profile_path) {
                return null;
              }
              return (
                <div key={id}>
                  <div
                    className={css.Carousel}
                    style={{
                      background: ` url('https://image.tmdb.org/t/p/original${profile_path}')`,
                      backgroundRepeat: 'noRepeat',
                      backgroundPosition: 'center',
                      backgroundSize: 'cover',
                    }}
                  >
                    <div className={css.box}>
                      <p>{name}</p>
                      <p>Character: {character}</p>
                    </div>
                  </div>
                </div>
              );
            })}
        </Carousel>
      )}
      {actors?.length > 0 &&
        actors.map(({ id, name, profile_path, character }) => {
          return (
            <Card
              key={id}
              hoverable
              style={{
                width: 200,
                height: 370,
              }}
            >
              {profile_path ? (
                <Image
                  src={`https://image.tmdb.org/t/p/w500/${profile_path}`}
                />
              ) : (
                <Image
                  src="error"
                  fallback="data:image/png;base64,"
                />
              )}

              <p>{name}</p>
              <p>Character: {character}</p>
            </Card>
          );
        })}
      {actors?.length < 1 && (
        <Empty className={css.Empty} description={<span>No Reviews</span>} />
      )}
    </div>
  );
};

export default Cast;
